// Generate movie ID starting from after real movies
let movieIdCounter = 100;

function generateMovieId() {
    return ++movieIdCounter;
}

function getRandomItem(arr) {
    return arr[Math.floor(Math.random() * arr.length)];
}

function getRandomItems(arr, count) {
    const shuffled = [...arr].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
}

function getRandomYear() {
    return Math.floor(Math.random() * 35) + 1990; // 1990-2024
}

function getRandomRating() {
    return Math.round((Math.random() * 3 + 5.5) * 10) / 10; // 5.5 to 8.5
}

function generatePosterUrl(title, language) {
    // Use picsum.photos for reliable random movie-like images
    // Each unique title gets a consistent image based on hash
    const hash = title.split('').reduce((a, b) => { a = ((a << 5) - a) + b.charCodeAt(0); return a & a; }, 0);
    const seed = Math.abs(hash) % 1000;
    return `https://picsum.photos/seed/${seed}/300/450`;
}

function generateSummary(title, genres, director) {
    const templates = [
        `A gripping ${genres[0]?.toLowerCase() || 'film'} that follows an unforgettable journey of discovery and transformation.`,
        `Directed by ${director}, this ${genres[0]?.toLowerCase() || 'movie'} takes viewers on an emotional roller coaster.`,
        `An acclaimed ${genres[0]?.toLowerCase() || 'film'} featuring powerful performances and stunning visuals.`,
        `A masterfully crafted ${genres[0]?.toLowerCase() || 'story'} that explores themes of love, loss, and redemption.`,
        `This ${genres[0]?.toLowerCase() || 'cinematic'} experience delivers heart-pounding action and memorable characters.`
    ];
    return getRandomItem(templates);
}

function generateMoviesForLanguage(langKey, count) {
    const template = movieTemplates[langKey];
    if (!template) return [];

    const movies = [];
    for (let i = 0; i < count; i++) {
        const director = getRandomItem(template.directors);
        const movieGenres = getRandomItems(genres, Math.floor(Math.random() * 3) + 1);
        const actors = getRandomItems(template.actors, Math.floor(Math.random() * 2) + 2);
        const musicDirector = getRandomItem(template.musicDirectors);
        const year = getRandomYear();
        const titleBase = getRandomItem(template.titles);
        const title = `${titleBase} ${Math.random() > 0.5 ? year.toString().slice(-2) : ''}`.trim();

        movies.push({
            id: generateMovieId(),
            title: title,
            year: year,
            language: langKey.charAt(0).toUpperCase() + langKey.slice(1),
            industry: template.industry,
            director: director,
            actors: actors,
            musicDirector: musicDirector,
            genres: movieGenres,
            rating: getRandomRating(),
            poster: generatePosterUrl(title, langKey),
            summary: generateSummary(title, movieGenres, director)
        });
    }
    return movies;
}

